```C#
public class C
{
    public IEnumerable<int> M1()
    {
        int[] array = new int[4];
        RuntimeHelpers.InitializeArray(array, (RuntimeFieldHandle)/*OpCode not supported: LdMemberToken*/);
        return new <>z__ReadOnlyArray<int>(array);
    }

    public IEnumerable<int> M2()
    {
        int[] array = new int[4];
        RuntimeHelpers.InitializeArray(array, (RuntimeFieldHandle)/*OpCode not supported: LdMemberToken*/);
