    );
    // to do a thread safe collection.
    private SharedProjectDocumentInfo? _sharedProjectDocumentInfo;
    public Func<CodeAnalysis.Project, IVsHierarchy>? HierarchyHelper { get; set; }

    public async Task NewDocumentAsync(CodeAnalysis.Document existingDocument, CodeAnalysis.Document newDocument)
    {
        if (newDocument.FilePath == null)
        {
            return;
        }
        var hierarchy = HierarchyHelper!(existingDocument.Project);
        var sharedHierarchy = sharedProjectService.GetSharedHierarchy(hierarchy, existingDocument);
        if (sharedHierarchy != null)
        {
            var projItemsPath = await sharedProjectService.GetProjItemsPathAsync(sharedHierarchy).ConfigureAwait(false);
            if (sharedHierarchy.TryGetProject(out var sharedProjectEnvDTE))
            {
                if (sharedProjectEnvDTE.TryGetFullPath(out var sharedProjectFullPath))
                {
                    _sharedProjectDocumentInfo = new SharedProjectDocumentInfo(
                        sharedProjectFullPath,
                        newDocument.FilePath!,
                        projItemsPath
                        );
                }
            }
        }
    }

    public bool TryAddDocument(string filePath, SourceText initialText)
    {
        if (_sharedProjectDocumentInfo?.SharedDocumentFilePath == filePath)
        {
            var sharedProjectDirectoryPath = _sharedProjectDocumentInfo!.SharedProjectFullPath;
            var projItemsPath = _sharedProjectDocumentInfo.ProjItemsPath ?? new DirectoryInfo(sharedProjectDirectoryPath).GetFiles("*.projitems", SearchOption.TopDirectoryOnly).FirstOrDefault()?.FullName;
            if (projItemsPath != null)
            {
                WriteFile();
                UpdateProjItemsFile(sharedProjectDirectoryPath, projItemsPath);
            }
            return true;
        }
        return false;

        void UpdateProjItemsFile(string sharedProjectDirectoryPath, string projItemsPath)
        {
            var itemRelativePath = GetRelativePath(sharedProjectDirectoryPath, projItemsPath);

            var projItemsDocument = XDocument.Parse(File.ReadAllText(projItemsPath));
            var ns = projItemsDocument.Root?.Name.Namespace ?? "http://schemas.microsoft.com/developer/msbuild/2003";
            projItemsDocument.Root?.Add(CreateItemGroupWithCompile(itemRelativePath, ns));
            projItemsDocument.Save(projItemsPath);
        }

        XElement CreateItemGroupWithCompile(string itemRelativePath, XNamespace ns)
        {
            return new XElement(ns + "ItemGroup",
                new XElement(ns + "Compile",
                    new XAttribute("Include", itemRelativePath)));
        }

        string GetRelativePath(string sharedProjectDirectoryPath, string projItemsPath)
        {
            if (PathUtilities.IsChildPath(sharedProjectDirectoryPath, filePath))
            {
                return $"$(MSBuildThisFileDirectory){PathUtilities.GetRelativePath(sharedProjectDirectoryPath, filePath)}";
            }
            else
            {
                return PathUtilities.GetRelativePath(projItemsPath, filePath);
            }
        }

        void WriteFile()
        {
            using var writer = new StreamWriter(filePath, append: false, encoding: initialText.Encoding ?? Encoding.UTF8);
            initialText.Write(writer);
        }
    }

    public void Clear()
    {
        _sharedProjectDocumentInfo = null;
    }

    public bool IsSharedDocument(Document document)
    {
        return sharedProjectService.GetSharedHierarchy(HierarchyHelper!(document.Project), document) is not null;
    }
