{
}

// entity base class which should assign a default value to the Id at creation.
// Two IsTransient methods to check if it has already been persisted. 
// The first try, just comparing the Id to TIdentifier.Default leads to a compilation error (see IsTransientBug)
// I have to first cast both sides of the == operator to IIdentifier to make it compile
public abstract class Entity<TIdentifier>
    where TIdentifier : IIdentifier, IDefaultable<TIdentifier>
{
    public virtual TIdentifier Id { get; protected set; } = TIdentifier.Default;

    // this works
    public bool IsTransient => (IIdentifier)Id == (IIdentifier)TIdentifier.Default;

    // Error CS0019  Operator '==' cannot be applied to operands of type 'TIdentifier' and 'TIdentifier'
    public bool IsTransientBug => Id == TIdentifier.Default;
}
```

