        using (Workspace workspace = new AdhocWorkspace())
        {
            string v = AppContext.GetData("TRUSTED_PLATFORM_ASSEMBLIES").ToString();
            IEnumerable<PortableExecutableReference> metadataReferences = v.Split(';').Select(f => MetadataReference.CreateFromFile(f));
            Project project = workspace.CurrentSolution.AddProject("Test", "Test", LanguageNames.CSharp).WithMetadataReferences(metadataReferences);
            var compilationOptions = ((CSharpCompilationOptions)project.CompilationOptions).WithOutputKind(OutputKind.DynamicallyLinkedLibrary).WithNullableContextOptions(NullableContextOptions.Enable);
            var parseOptions = ((CSharpParseOptions)project.ParseOptions).WithLanguageVersion(LanguageVersion.Latest);
            project = project.WithCompilationOptions(compilationOptions).WithParseOptions(parseOptions);
            Document document = project.AddDocument("Document", SourceText.From(source));
            SemanticModel semanticModel = await document.GetSemanticModelAsync();
            SyntaxTree tree = await document.GetSyntaxTreeAsync();
            SyntaxNode root = await tree.GetRootAsync();

            var forEachStatement = (ForEachStatementSyntax)root.DescendantNodes().First(f => f.IsKind(SyntaxKind.ForEachStatement));
            var forEachStatementInfo = semanticModel.GetForEachStatementInfo(forEachStatement);
            TypeInfo typeInfo = semanticModel.GetTypeInfo(forEachStatement.Expression);

            Console.WriteLine(forEachStatementInfo.ElementType.ToDisplayString(SymbolDisplayFormat.CSharpErrorMessageFormat));
            Console.WriteLine(typeInfo.Type.ToDisplayString(SymbolDisplayFormat.CSharpErrorMessageFormat));
        }
    }
}
