using BenchmarkDotNet.Attributes;
using BenchmarkDotNet.Running;
using Proto.Promises;
using Proto.Promises.CompilerServices;
using System.Numerics;
using System.Runtime.CompilerServices;
using System.Runtime.InteropServices;

namespace AsyncMethodBuilderExperiment
{
    internal class Program
    {
        static void Main(string[] args)
        {
            BenchmarkSwitcher.FromAssembly(typeof(Program).Assembly).RunAllJoined(args: args);
        }
    }

    public class AsyncHelper
    {
        private readonly bool _isPending;
        private readonly Stack<Promise.Deferred> _deferreds;

        public AsyncHelper(bool isPending)
        {
            _isPending = isPending;
            _deferreds = new Stack<Promise.Deferred>(2);
        }

        public void ResolvePending()
        {
            while (_deferreds.Count > 0)
            {
                _deferreds.Pop().Resolve();
            }
        }

        public Promise GetBasePromise()
        {
            if (!_isPending)
            {
                return Promise.Resolved();
            }
            var deferred = Promise.NewDeferred();
            _deferreds.Push(deferred);
            return deferred.Promise;
        }
    }

    [MemoryDiagnoser(false)]
    [DisassemblyDiagnoser(100)]
    [GenericTypeArguments(typeof(object))]
    [GenericTypeArguments(typeof(Matrix4x4))]
    public class AsyncBenchmarks<TResult>
    {
        [ParamsAllValues]
        public bool Pending { get; set; }

        private AsyncHelper? _protoHelper;

        [GlobalSetup]
        public void Setup()
        {
            Promise.Config.ObjectPoolingEnabled = false;
            _protoHelper = new(Pending);
        }

        [Benchmark]
        public void OldAsync()
        {
            GetValueAsyncOld().Forget();
            _protoHelper!.ResolvePending();
        }

        [Benchmark]
        public void NewAsync()
        {
            GetValueAsyncNew().Forget();
            _protoHelper!.ResolvePending();
        }

        //private async Promise<TResult> GetValueAsync()
        //{
        //    await _protoHelper!.GetBasePromise();
        //    return default!;
        //}

        [StructLayout(LayoutKind.Auto)]
        private struct GetValueAsyncOldMachine : IAsyncStateMachine
        {
            public int __state;

            public AsyncPromiseMethodBuilder<TResult> t__builder;

            public AsyncBenchmarks<TResult> __this;

            private PromiseAwaiterVoid u__1;

            public void MoveNext()
            {
                int num = __state;
                AsyncBenchmarks<TResult> asyncBenchmarks = __this;
                TResult result;
                try
                {
                    PromiseAwaiterVoid awaiter;
                    if (num != 0)
                    {
                        awaiter = asyncBenchmarks._protoHelper!.GetBasePromise().GetAwaiter();
                        if (!awaiter.IsCompleted)
                        {
                            num = (__state = 0);
                            u__1 = awaiter;
                            t__builder.AwaitUnsafeOnCompleted(ref awaiter, ref this);
                            return;
                        }
                    }
                    else
                    {
                        awaiter = u__1;
                        u__1 = default;
                        num = (__state = -1);
                    }
                    awaiter.GetResult();
                    result = default!;
                }
                catch (Exception exception)
                {
                    __state = -2;
                    t__builder.SetException(exception);
                    return;
                }
                __state = -2;
                t__builder.SetResult(result);
            }

            void IAsyncStateMachine.SetStateMachine(IAsyncStateMachine stateMachine) => throw new NotImplementedException();
        }

        public Promise<TResult> GetValueAsyncOld()
        {
            GetValueAsyncOldMachine stateMachine = default;
            stateMachine.t__builder = AsyncPromiseMethodBuilder<TResult>.Create();
            stateMachine.__this = this;
            stateMachine.__state = -1;
            stateMachine.t__builder.Start(ref stateMachine);
            return stateMachine.t__builder.Task;
        }

        [StructLayout(LayoutKind.Auto)]
        private struct GetValueAsyncNewHeapMachine : IAsyncStateMachine
        {
            public int __state;

            public AsyncPromiseMethodBuilderNew<TResult> t__builder;

            public AsyncBenchmarks<TResult> __this;

            public PromiseAwaiterVoid u__1;

            public void MoveNext()
            {
                int num = __state;
                AsyncBenchmarks<TResult> asyncBenchmarks = __this;
                TResult result;
                try
                {
                    PromiseAwaiterVoid awaiter;
                    if (num != 0)
                    {
                        awaiter = asyncBenchmarks._protoHelper!.GetBasePromise().GetAwaiter();
                        if (!awaiter.IsCompleted)
                        {
                            num = (__state = 0);
                            u__1 = awaiter;
                            t__builder.AwaitUnsafeOnCompleted(ref awaiter);
                            return;
                        }
                    }
                    else
                    {
                        awaiter = u__1;
                        u__1 = default;
                        num = (__state = -1);
                    }
                    awaiter.GetResult();
                    result = default!;
                }
                catch (Exception exception)
                {
                    __state = -2;
                    t__builder.SetException(exception);
                    return;
                }
                __state = -2;
                t__builder.SetResult(result);
            }

            void IAsyncStateMachine.SetStateMachine(IAsyncStateMachine stateMachine) => throw new NotImplementedException();
        }

        [StructLayout(LayoutKind.Auto)]
        private struct GetValueAsyncNewStackMachine : IAsyncStateMachine
        {
            public int __state;

            public AsyncPromiseMethodBuilderNew<TResult> t__builder;

            public AsyncBenchmarks<TResult> __this;

            private PromiseAwaiterVoid u__1;

            public Promise<TResult> __task;

            public void MoveNext()
            {
                int num = __state;
                AsyncBenchmarks<TResult> asyncBenchmarks = __this;
                TResult result;
                try
                {
                    PromiseAwaiterVoid awaiter;
                    if (num != 0)
                    {
                        awaiter = asyncBenchmarks._protoHelper!.GetBasePromise().GetAwaiter();
                        if (!awaiter.IsCompleted)
                        {
                            num = (__state = 0);
                            u__1 = awaiter;
                            // New code, create the heap version of the state machine and pass it to the builder to get the pending task before calling AwaitUnsafeOnCompleted.
                            GetValueAsyncNewHeapMachine stateMachine = default;
                            stateMachine.t__builder = t__builder;
                            stateMachine.__this = __this;
                            stateMachine.__state = __state;
                            stateMachine.u__1 = u__1;
                            __task = stateMachine.t__builder.GetPendingTask(ref stateMachine);
                            stateMachine.t__builder.AwaitUnsafeOnCompleted(ref awaiter);
                            return;
                        }
                    }
                    else
                    {
                        awaiter = u__1;
                        u__1 = default;
                        num = (__state = -1);
                    }
                    awaiter.GetResult();
                    result = default!;
                }
                catch (Exception exception)
                {
                    __state = -2;
                    //t__builder.SetException(exception);
                    __task = t__builder.GetTaskFromException(exception);
                    return;
                }
                __state = -2;
                //t__builder.SetResult(result);
                __task = t__builder.GetCompletedTask(result);
            }

            void IAsyncStateMachine.SetStateMachine(IAsyncStateMachine stateMachine) => throw new NotImplementedException();
        }

        public Promise<TResult> GetValueAsyncNew()
        {
            GetValueAsyncNewStackMachine stateMachine = default;
            stateMachine.t__builder = AsyncPromiseMethodBuilderNew<TResult>.Create();
            stateMachine.__this = this;
            stateMachine.__state = -1;
            stateMachine.t__builder.Start(ref stateMachine);
            return stateMachine.__task;
        }
    }
}
```

