var asmASym = (IAssemblySymbol)compB.GetAssemblyOrModuleSymbol(asmARef)!;
var myTypeSym = asmASym.GetTypeByMetadataName("MyType")!;
Console.WriteLine(compB.IsSymbolAccessibleWithin(myTypeSym, compB.Assembly));
