static void Main(string[] args)
 {
     Console.WriteLine("begin!");

     //        // create MLContext
     var context = new MLContext();

     // load
     var data = LoadData(context);

     try
     {
         // train
         var pipeline = context.Transforms.Text.FeaturizeText("Features", nameof(InputData.Text))
               .Append(context.MulticlassClassification.Trainers.SdcaMaximumEntropy("Label", "Features"))
             .Append(context.Transforms.Conversion.MapValueToKey("PredictedLabel"));


         var model = pipeline.Fit(data);


         // test
         var testData = new List<InputData>
             {
                 new InputData { Text = "The latest advancements in AI technology." },
                 new InputData { Text = "The football match was thrilling!" },
                 new InputData { Text = "New smartphone models are being released." }
             };

         var predictions = model.Transform(context.Data.LoadFromEnumerable(testData));
         var results = context.Data.CreateEnumerable<OutputData>(predictions, reuseRowObject: false);
         // result
         foreach (var prediction in results)
         {
             Console.WriteLine($"Predicted Label: {prediction.PredictedLabel}");
         }
     }
     catch (Exception ex)
     {

         File.WriteAllText(AppDomain.CurrentDomain.BaseDirectory + "/log/" + DateTime.Now.ToString("yyyyMMddHHmmss") + ".txt", ex.Message + "\r\n" + ex.InnerException);
     }

     Console.ReadKey();
 }

