using Microsoft.ML.Tokenizers;
using System.Text.Json;

class program
{
    const string IMStart = "<|im_start|>";
    const string IMEnd = "<|im_end|>";

    public static void Main()
    {
        string text = $"{IMEnd}\n{IMStart}assistant\n<think>\n\n</think>\n\n";
        string text1 = "<|im_start|>system\nJudge whether the Document meets the requirements based on the Query and the Instruct provided. Note that the answer can only be '<|im_end|>\n<|im_start|>user\n";
        text1 = text1.Replace(" ", "\u0120").Replace("\n", "\u010A");
        using Stream vocabStream = File.OpenRead("D:\\model\\Qwen3-Reranker-0.6B-ONNX\\vocab.json");
        using Stream mergesStream = File.OpenRead("D:\\model\\Qwen3-Reranker-0.6B-ONNX\\merges.txt");
        string configPath = "D:\\model\\Qwen3-Reranker-0.6B-ONNX\\tokenizer_config.json";
        var specialTokens = LoadSpecialTokens(configPath);

        var bpe = BpeTokenizer.Create(vocabStream, mergesStream, PreTokenizer.CreateWordOrNonWord(specialTokens), specialTokens:specialTokens);
        var encoding = bpe.EncodeToTokens(text1, out _);
        Console.WriteLine("\nEncoded IDs:");
        foreach (var id in encoding)
        {
            Console.WriteLine($"{id.Id} -> {id.Value.Replace("\u010A", "\n").Replace("\u0120", " ")}");
        }
    }

    private static Dictionary<string, int> LoadSpecialTokens(string configPath)
    {
        if (!File.Exists(configPath))
        {
            throw new FileNotFoundException($"Config file not found: {configPath}");
        }

        var json = File.ReadAllText(configPath);
        using JsonDocument doc = JsonDocument.Parse(json);
        JsonElement root = doc.RootElement;

        // 创建特殊标记字典
        var specialTokens = new Dictionary<string, int>();

        // 1. 首先添加 additional_special_tokens
        if (root.TryGetProperty("additional_special_tokens", out JsonElement additionalTokens))
        {
            foreach (JsonElement token in additionalTokens.EnumerateArray())
            {
                string content = token.GetString();
                if (!string.IsNullOrEmpty(content))
                {
                    // 查找在 added_tokens_decoder 中对应的 ID
                    int? tokenId = FindTokenIdInDecoder(root, content);

                    if (tokenId.HasValue)
                    {
                        // 使用 added_tokens_decoder 中的 ID
                        specialTokens[content] = tokenId.Value;
                    }
                    else
                    {
                        // 分配新 ID（在原有最大ID基础上增加）
                        int newId = specialTokens.Count > 0
                            ? specialTokens.Values.Max() + 1
                            : 1000000;
                        specialTokens[content] = newId;
                    }
                }
            }
        }

        // 2. 添加 added_tokens_decoder 中的其他标记（不在 additional_special_tokens 中的）
        if (root.TryGetProperty("added_tokens_decoder", out JsonElement addedTokens))
        {
            foreach (JsonProperty prop in addedTokens.EnumerateObject())
            {
                if (int.TryParse(prop.Name, out int tokenId))
                {
                    string content = prop.Value.GetProperty("content").GetString();
                    if (!string.IsNullOrEmpty(content)
                        && !specialTokens.ContainsKey(content))
                    {
                        specialTokens[content] = tokenId;
                    }
                }
            }
        }

        // 3. 确保关键标记存在
        EnsureSpecialToken(specialTokens, "<|endoftext|>", 151643);
        EnsureSpecialToken(specialTokens, "<|im_start|>", 151644);
        EnsureSpecialToken(specialTokens, "<|im_end|>", 151645);

        //Console.WriteLine("Loaded special tokens:");
        //foreach (var kvp in specialTokens)
        //{
        //    Console.WriteLine($"{kvp.Value}: {kvp.Key}");
        //}

        return specialTokens;
    }

    private static int? FindTokenIdInDecoder(JsonElement root, string tokenContent)
    {
        if (root.TryGetProperty("added_tokens_decoder", out JsonElement addedTokens))
        {
            foreach (JsonProperty prop in addedTokens.EnumerateObject())
            {
                if (int.TryParse(prop.Name, out int tokenId))
                {
                    string content = prop.Value.GetProperty("content").GetString();
                    if (content == tokenContent)
                    {
                        return tokenId;
                    }
                }
            }
        }
        return null;
    }

    private static void EnsureSpecialToken(Dictionary<string, int> specialTokens, string token, int defaultId)
    {
        if (!specialTokens.ContainsKey(token))
        {
            specialTokens[token] = defaultId;
        }
    }
}
