            _data = _mlContext.Data.LoadFromTextFile<CatInput>(path: "cat", hasHeader: false, separatorChar: ',');
            IEstimator<ITransformer> _pipeline = _mlContext.Transforms.Categorical.OneHotHashEncoding(
                inputColumnName: "input",
