            IDataView trainingData = mlContext.Data.LoadFromTextFile<DataSchema>($"../RankingData/Train/RankingData_Train_RandOrder.tsv", loaderOptions);
            IDataView testData = mlContext.Data.LoadFromTextFile<DataSchema>($"../RankingData/Test/RankingData_Test.tsv", loaderOptions);

            var pipeline = mlContext.Transforms.DropColumns(new string[] {"C1"})
                           .Append(mlContext.Transforms.Categorical.OneHotHashEncoding("GroupId", outputKind: OneHotEncodingEstimator.OutputKind.Key))
                           .Append(mlContext.Transforms.Conversion.MapValueToKey("Label"))
                           .Append(mlContext.Ranking.Trainers.LightGbm(trainerOptions));

            var model = pipeline.Fit(trainingData);

            var predictions = model.Transform(testData);
            var metrics = mlContext.Ranking.Evaluate(predictions, evaluatorOptions,
                                                     labelColumnName: "Label", 
                                                     rowGroupColumnName: "GroupId",
                                                     scoreColumnName: "Score");

            mlContext.Model.Save(model, trainingData.Schema, $"../Checkpoints/LightGbm.zip");

            return metrics.NormalizedDiscountedCumulativeGains;
        }
