using Microsoft.ML;
using Microsoft.ML.Data;
using Microsoft.ML.Transforms.Onnx;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace testonnx
{
    public class OutputData
    {
        [ColumnName("output_probability")]
        [OnnxSequenceType(typeof(IDictionary<long, float>))]
        public IEnumerable<IDictionary<long, float>> OutputProbabilities;
    }

    public class InputData
    {
        [ColumnName("float_input")]
        [VectorType(4)]
        public float[] Input { get; set; }
    }

    class Program
    {
        static void Main(string[] args)
        {
            var outputSchemaDef = SchemaDefinition.Create(typeof(OutputData));
            var inputSchemaDef = SchemaDefinition.Create(typeof(InputData));
            var mlContext = new MLContext();
            var pipeline = mlContext.Transforms
               .ApplyOnnxModel(new string[] { "output_probability", "output_label" }, new string[] { "float_input" }, "rf_iris_new.onnx");

            var inputs = new List<InputData>() { new InputData() { Input = new float[] { 3.1f, 0.9f, 1.2f, 0.8f } } };

            var data = mlContext.Data.LoadFromEnumerable(inputs, inputSchemaDef);

            var model = pipeline.Fit(data);

            var transformedValues = model.Transform(data);

            var predictions = mlContext.Data.CreateEnumerable<OutputData>(
                transformedValues, reuseRowObject: false, true, outputSchemaDef);

            foreach (var pred in predictions) 
            {
                Console.WriteLine(pred.OutputProbabilities.First()[0].ToString());              
            }
        }
    }
}

