using System;
using System.IO;
using DocumentFormat.OpenXml.Packaging;
using DocumentFormat.OpenXml.Presentation;
using A = DocumentFormat.OpenXml.Drawing;

class Program
{
    static void Main(string[] args)
    {
        string filePath = "sample.pptx";

        using (PresentationDocument presentationDoc = PresentationDocument.Create(filePath, DocumentFormat.OpenXml.PresentationDocumentType.Presentation))
        {
            // Add the presentation part
            PresentationPart presentationPart = presentationDoc.AddPresentationPart();
            presentationPart.Presentation = new Presentation();

            // Add slide master part and slide layout part
            SlideMasterPart slideMasterPart = presentationPart.AddNewPart<SlideMasterPart>();
            slideMasterPart.SlideMaster = new SlideMaster(new SlideLayoutIdList(), new TextStyles());

            SlideLayoutPart slideLayoutPart = slideMasterPart.AddNewPart<SlideLayoutPart>();
            slideLayoutPart.SlideLayout = new SlideLayout(new CommonSlideData(new ShapeTree()));

            // Link slide layout to slide master
            var slideLayoutRelId = slideMasterPart.GetIdOfPart(slideLayoutPart);
            slideMasterPart.SlideMaster.SlideLayoutIdList = new SlideLayoutIdList(
                new SlideLayoutId() { Id = 1U, RelationshipId = slideLayoutRelId }
            );

            // Add slide part
            SlidePart slidePart = presentationPart.AddNewPart<SlidePart>();
            slidePart.Slide = new Slide(new CommonSlideData(new ShapeTree()));

            // Link slide to slide layout
            var slideRelId = slidePart.AddPart(slideLayoutPart);

            // Add slide ID to presentation
            SlideIdList slideIdList = new SlideIdList();
            uint slideId = 256U;
            slideIdList.Append(new SlideId() { Id = slideId, RelationshipId = presentationPart.GetIdOfPart(slidePart) });

            presentationPart.Presentation.Append(slideIdList);
            presentationPart.Presentation.Save();
        }

        Console.WriteLine($"Presentation created: {Path.GetFullPath("sample.pptx")}");
    }
}
