    //bug236
    range = range.Replace("$", String.Empty);

    if (range != string.Empty)
    {
        srange = range.Split(':');

        if (range.Contains(':'))
        {
            startCell = srange[0];
            endCell = srange[1];
        }
        else
        {
            startCell = srange[0];
            endCell = srange[0];
        }

        for (int x = 0; x < rows.Count(); x++)
        {
            foreach (DocumentFormat.OpenXml.Spreadsheet.Cell c in rows[x].Elements<DocumentFormat.OpenXml.Spreadsheet.Cell>().ToArray())
