```
import { StrictMode, Suspense } from 'react';
import ReactDOM from 'react-dom/client';
import { QueryClientProvider } from '@tanstack/react-query';
import { queryClient } from '@boa/queries';
import './main.css';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { CommandMenuProvider } from '@boa/shared-ui';
import { AuthProvider } from 'react-oidc-context';
import { onSigninCallback, userManager } from './config/config';
import UserProvider from '@boa/app-boa/UserProvider';
import RouterWrapper from '@boa/shared-routes/RouterWrapper';

ReactDOM.createRoot(document.getElementById('root')!).render(
    <StrictMode>
        <Suspense>
            <AuthProvider userManager={userManager} onSigninCallback={onSigninCallback}>
                <QueryClientProvider client={queryClient}>
                    <UserProvider>
                        <CommandMenuProvider>
                            <RouterWrapper />
                        </CommandMenuProvider>
                    </UserProvider>
                    <ToastContainer />
                </QueryClientProvider>
            </AuthProvider>
        </Suspense>
    </StrictMode>,
);
```

