  label: string,
  iconClass: string,
}


const routes:  RouteRecordRaw<RouteMeta>[] = [
  {
    path: '/',
    name: 'Home',
    component: Home,
    meta: {
      label: 'My Home',
      iconClass: 'home-icon',
