import { NAMESPACE } from "./types";

// Key point !
const { mapState, mapGetters, mapActions } = createNamespacedHelpers(NAMESPACE);

export { mapState, mapGetters, mapActions };

export default { /* namespaced, state, getters, mutations, actions */ }
```

