const LayoutView = () => import("@/components/LayoutView/index.vue");
const WelcomeIndex= () => import("@/pages/welcome/views/home/index.vue");

router.addRoutes([{
    path: '/',
    component: LayoutView,
    children: [{
        path: 'index',
        name: 'welcomeIndex',
