    [Benchmark(OperationsPerInvoke = Iterations, Baseline = true)]
    [Arguments(123.45)]
    [Arguments(double.NaN)]
    public bool Clr(double value)
    {
        bool res = false;

        for (int i = Iterations; i != 0; i--)
        {
            res = double.IsNaN(value);
        }

        return res;
    }

    [Benchmark(OperationsPerInvoke = Iterations)]
    [Arguments(123.45)]
    [Arguments(double.NaN)]
    public bool Wpf(double value)
    {
        bool res = false;
        
        for (int i = Iterations; i != 0 ; i--)
        {
            res = IsNaN(value);
        }
        
        return res;
    }

    public static bool IsNaN(double value)
    {
        NanUnion t = new NanUnion();
        t.DoubleValue = value;

        ulong exp = t.UintValue & 0xfff0000000000000;
        ulong man = t.UintValue & 0x000fffffffffffff;

        return (exp == 0x7ff0000000000000 || exp == 0xfff0000000000000) && (man != 0);
    }

    [StructLayout(LayoutKind.Explicit)]
    private struct NanUnion
    {
        [FieldOffset(0)]
        internal double DoubleValue;
        [FieldOffset(0)]
        internal ulong UintValue;
    }
}
